@Component({
  selector: 'bb-agency-card',
  templateUrl: './bb-agency-card.component.html',
  styleUrls: ['./bb-agency-card.component.scss'],
})
export class BbAgencyCardComponent {
  /**
   * Nome ou número
   *
   * @required
   */
  @Input()
  name: string = '';

  /**
   * Telefone com DDD
   *
   * @optional
   */
  @Input()
  tel?: string = '';

  /**
   * Endereço completo
   *
   * @optional
   */
  @Input()
  address?: string = '';

  /**
   * Horário de funcionamento
   *
   * @optional
   */
  @Input()
  openingHours?: string = '';

  /**
   * Serviços ofertados
   *
   * @optional
   */
  @Input()
  services?: string[];

  /**
   * URL do mapa
   *
   * @optional
   */
  @Input()
  mapUrl?: string = '';

  /**
   * Serviços ofertados
   *
   * @optional
   */
  @Input()
  type?: 'default' | 'estilo' | 'private' = 'default';

  /**
   * @internal
   */
  constructor() {}

  /**
   * @internal
   */
  ngOnInit(): void {}

  get attrName(){
    return `Ver ${this.name} no mapa`;
  }
}

