    top: calc(100% - 30px);
    left: 0;
    min-width: 100%;
    box-sizing: border-box;
    background-color: white;
    z-index: 0;
    border-radius: var(--card-elastic-borderRadius);
    transition: box-shadow 100ms linear, max-height 400ms ease-in-out;
    overflow: hidden;
    --maxHeight: 0;
